// src/components/TelemetryPanel.js
import React from 'react';
import {
  CircularProgressbar,
  buildStyles,
} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

import {
  Box,
  Card,
  CardContent,
  Typography,
  Stack,
  Chip,
} from '@mui/material';

const getColor = (value, type) => {
  if (type === 'battery') {
    if (value < 3.7) return 'error';
    if (value < 7) return 'warning';
    return 'primary';
  } else if (type === 'temp') {
    if (value > 70) return 'error';
    if (value > 50) return 'warning';
    return 'primary';
  } else if (type === 'connection') {
    if (value === 'Excellent') return 'success';
    if (value === 'Poor') return 'warning';
    return 'error';
  }
};

const TelemetryPanel = ({ telemetry }) => {
  if (!telemetry) return <Typography color="gray">Waiting for telemetry...</Typography>;

  const {
    battery,
    temperature,
    connection,
    gps,
    altitude,
    roll,
    pitch,
    yaw,
  } = telemetry;

  return (
    <Box
      sx={{
        height: 'calc(100vh - 64px)',
        overflowY: 'auto',
        px: 2,
        py: 2,
        scrollBehavior: 'smooth',
        scrollbarWidth: 'none',
        '&::-webkit-scrollbar': { display: 'none' },
      }}
    >
         <Stack spacing={2} alignItems="center" justifyContent="flex-start">

        {/* Battery Gauge */}
        <Card sx={{ width: '100%', maxWidth: 400, textAlign: 'center', py: 2 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üîã Battery</Typography>
            <Box sx={{ width: 200, mx: 'auto', mt: 2 }}>
              <CircularProgressbar
                value={(battery / 12) * 100}
                text={`${battery.toFixed(2)}V`}
                styles={buildStyles({
                  pathColor: battery < 3.7 ? '#f44336' : battery < 7 ? '#ff9800' : '#1976d2',
                  textColor: '#fff',
                  trailColor: '#333',
                })}
              />
            </Box>
          </CardContent>
        </Card>

        {/* Connection */}
        <Card sx={{ width: '100%', maxWidth: 400 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üì° Connection</Typography>
            <Chip
              label={connection}
              color={getColor(connection, 'connection')}
              sx={{ mt: 2, width: '75%', textAlign: 'center' }}
            />
          </CardContent>
        </Card>

        {/* GPS */}
        <Card sx={{ width: '100%', maxWidth: 400 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üìç GPS</Typography>
            <Typography variant="body1">
              Lat: {gps?.lat?.toFixed(4)}, 
            </Typography>
            <Typography variant="body1">
                 :   
            </Typography>
            <Typography variant="body1">
              Lon: {gps?.lon?.toFixed(4)}
            </Typography>
            <Typography variant="body2">
              Alt: {gps?.alt?.toFixed(2)} m
            </Typography>
          </CardContent>
        </Card>

        {/* IMU - Roll, Pitch, Yaw */}
        <Card sx={{ width: '100%', maxWidth: 400 }}>
          <CardContent>
            <Typography variant="h6" color="primary" sx={{ mb: 2 }}>üìà IMU</Typography>
            <Stack direction="row" spacing={2} justifyContent="center">
              {[
                { label: 'Roll', value: roll, color: '#00bcd4' },
                { label: 'Pitch', value: pitch, color: '#8bc34a' },
                { label: 'Yaw', value: yaw, color: '#ff5722' },
              ].map(({ label, value, color }) => (
                <Box key={label} sx={{ width: 100, textAlign: 'center' }}>
                  <CircularProgressbar
                    value={(Math.abs(value) / 180) * 100}
                    text={`${value.toFixed(1)}¬∞`}
                    styles={buildStyles({
                      pathColor: color,
                      textColor: '#fff',
                      trailColor: '#222',
                    })}
                  />
                  <Typography variant="caption" color="gray">{label}</Typography>
                </Box>
              ))}
            </Stack>
          </CardContent>
        </Card>

        {/* Temperature Gauge */}
        <Card sx={{ width: '100%', maxWidth: 400, textAlign: 'center', py: 2 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üå°Ô∏è Temperature</Typography>
            <Box sx={{ width: 150, mx: 'auto', mt: 2 }}>
              <CircularProgressbar
                value={(temperature / 100) * 100}
                text={`${temperature.toFixed(1)}¬∞C`}
                styles={buildStyles({
                  pathColor: temperature > 70 ? '#f44336' : temperature > 50 ? '#ff9800' : '#1976d2',
                  textColor: '#fff',
                  trailColor: '#333',
                })}
              />
            </Box>
          </CardContent>
        </Card>

        {/* Altitude Gauge */}
        <Card sx={{ width: '100%', maxWidth: 400, textAlign: 'center', py: 2 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üß≠ Altitude</Typography>
            <Box sx={{ width: 150, mx: 'auto', mt: 2 }}>
              <CircularProgressbar
                value={Math.min((altitude / 1000) * 100, 100)}
                text={`${altitude.toFixed(1)}m`}
                styles={buildStyles({
                  pathColor: '#4caf50',
                  textColor: '#fff',
                  trailColor: '#333',
                })}
              />
            </Box>
          </CardContent>
        </Card>

      </Stack>
    </Box>
  );
};

export default TelemetryPanel;
