import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const bootSequence = [
  'Initializing Drone Command HQ...',
  'Powering up sensors...',
  'Loading Telemetry Core...',
  'Establishing satellite uplink...',
  'Activating visual interface...',
  'System check complete...',
  'Welcome, Commander.',
];

const BootScreen = ({ onComplete }) => {
  const [started, setStarted] = useState(false);
  const [audio, setAudio] = useState(null);

  useEffect(() => {
    if (started) {
      const bootAudio = new Audio('/sounds/boot.mp3');
      bootAudio.volume = 0.7;
      bootAudio.play().catch(err => console.warn('Audio failed:', err));
      setAudio(bootAudio);

      const totalDuration = bootSequence.length * 1000 + 1000;
      const timer = setTimeout(() => {
        onComplete();
      }, totalDuration);

      return () => {
        clearTimeout(timer);
        bootAudio.pause();
        bootAudio.currentTime = 0;
      };
    }
  }, [started, onComplete]);

  const handleClick = () => {
    if (!started) setStarted(true);
  };

  return (
    <div
      onClick={handleClick}
      style={{
        backgroundColor: 'black',
        color: '#00ffe0',
        fontFamily: 'Orbitron, monospace',
        fontSize: '1.2rem',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        paddingLeft: '5vw',
        cursor: started ? 'default' : 'pointer',
      }}
    >
      {!started ? (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
          style={{ textShadow: '0 0 8px cyan' }}
        >
          Tap to Initiate Boot Sequence
        </motion.div>
      ) : (
        bootSequence.map((line, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: -40 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: index, duration: 0.5 }}
            style={{ marginBottom: '0.9rem', textShadow: '0 0 10px cyan' }}
          >
            {line}
          </motion.div>
        ))
      )}
    </div>
  );
};

export default BootScreen;
