import React, { useEffect } from 'react';
import { motion } from 'framer-motion';

const bootSequence = [
  'Initializing Drone Command HQ...',
  'Powering up sensors...',
  'Loading Telemetry Core...',
  'Establishing satellite uplink...',
  'Activating visual interface...',
  'System check complete...',
  'Welcome, Commander.',
];

const BootScreen = ({ onComplete }) => {
  useEffect(() => {
    const audio = new Audio('/sounds/boot.mp3');
    audio.volume = 0.7;

    const playAudio = () => {
      audio.play().catch(err => console.error('Autoplay blocked:', err));
      window.removeEventListener('click', playAudio);
    };

    window.addEventListener('click', playAudio);

    const timer = setTimeout(() => {
      onComplete();
    }, bootSequence.length * 1000 + 1000); // slight buffer

    return () => {
      clearTimeout(timer);
      window.removeEventListener('click', playAudio);
    };
  }, [onComplete]);

  return (
    <div
      style={{
        backgroundColor: 'black',
        color: '#00ffe0',
        fontFamily: 'Orbitron, monospace',
        fontSize: '1.2rem',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        paddingLeft: '5vw',
      }}
    >
      {bootSequence.map((line, index) => (
        <motion.div
          key={index}
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: index, duration: 0.6 }}
          style={{ marginBottom: '0.8rem', textShadow: '0 0 8px cyan' }}
        >
          {line}
        </motion.div>
      ))}
    </div>
  );
};

export default BootScreen;
