import React, { useEffect, useRef, useState } from 'react';
import './BootScreen.css';

const bootSequence = [
  'Initializing Drone Command HQ...',
  'Powering up sensors...',
  'Loading Telemetry Core...',
  'Establishing satellite uplink...',
  'Activating visual interface...',
  'System check complete...',
  'Welcome, Commander.',
];

const BootScreen = ({ onComplete }) => {
  const audioRef = useRef(null);
  const [currentLine, setCurrentLine] = useState(0);
  const [displayedLines, setDisplayedLines] = useState([]);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    audioRef.current = new Audio('/sounds/boot.mp3');
    audioRef.current.volume = 0.7;
    audioRef.current.load();

    const interval = setInterval(() => {
      if (currentLine < bootSequence.length) {
        setDisplayedLines((prev) => [...prev, bootSequence[currentLine]]);
        setCurrentLine((prev) => prev + 1);
      } else {
        clearInterval(interval);
        setTimeout(() => {
          setFadeOut(true);
          audioRef.current.play().catch((err) => console.warn("Sound autoplay blocked:", err));
          setTimeout(() => {
            onComplete();
          }, 1000);
        }, 500);
      }
    }, 700); // Delay between each line

    return () => clearInterval(interval);
  }, [currentLine, onComplete]);

  return (
    <div className="boot-screen">
      {displayedLines.map((line, index) => (
        <div key={index} className="boot-line glitch typewriter">
          {line}
        </div>
      ))}

      {fadeOut && <div className="fade-out" />}
    </div>
  );
};

export default BootScreen;
