// src/components/TelemetryPanel.js
import React from 'react';
import {
  CircularProgressbar,
  buildStyles,
} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

import {
  Box,
  Card,
  CardContent,
  Typography,
  Stack,
  LinearProgress,
  Chip,
} from '@mui/material';

const getColor = (value, type) => {
  if (type === 'battery') {
    if (value < 3.7) return 'error';
    if (value < 7) return 'warning';
    return 'primary';
  } else if (type === 'temp') {
    if (value > 70) return 'error';
    if (value > 50) return 'warning';
    return 'primary';
  } else if (type === 'connection') {
    if (value === 'Excellent') return 'success';
    if (value === 'Poor') return 'warning';
    return 'error';
  }
};

const TelemetryPanel = ({ telemetry }) => {
  if (!telemetry) return <Typography color="gray">Waiting for telemetry...</Typography>;

  const {
    battery,
    temperature,
    connection,
    gps,
    altitude,
    roll,
    pitch,
    yaw,
  } = telemetry;

  return (
    <Box
      sx={{
        height: 'calc(100vh - 64px)', // adjust this height based on your TopBar
        overflowY: 'auto',
        px: 2,
        py: 2,
      }}
    >
      <Stack spacing={2} alignItems="center" justifyContent="flex-start">
        
        {/* Battery Circular Gauge */}
        <Card sx={{ width: '100%', maxWidth: 360, textAlign: 'center', py: 2 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üîã Battery</Typography>
            <Box sx={{ width: 100, mx: 'auto', mt: 2 }}>
              <CircularProgressbar
                value={(battery / 12) * 100}
                text={`${battery.toFixed(2)}V`}
                styles={buildStyles({
                  pathColor:
                    battery < 3.7
                      ? '#f44336'
                      : battery < 7
                      ? '#ff9800'
                      : '#1976d2',
                  textColor: '#fff',
                  trailColor: '#333',
                  backgroundColor: '#1a1a1a',
                })}
              />
            </Box>
          </CardContent>
        </Card>

        {/* Connection */}
        <Card sx={{ width: '100%', maxWidth: 360 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üì° Connection</Typography>
            <Chip
              label={connection}
              color={getColor(connection, 'connection')}
              sx={{ mt: 1 }}
            />
          </CardContent>
        </Card>

        {/* GPS */}
        <Card sx={{ width: '100%', maxWidth: 360 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üìç GPS</Typography>
            <Typography variant="body1">
              Lat: {gps?.lat?.toFixed(4)}, Lon: {gps?.lon?.toFixed(4)}
            </Typography>
            <Typography variant="body2">
              Alt: {gps?.alt?.toFixed(2)} m
            </Typography>
          </CardContent>
        </Card>

        {/* IMU */}
        <Card sx={{ width: '100%', maxWidth: 360 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üìà IMU</Typography>
            <Typography variant="body1">
              Roll: {roll?.toFixed(2)}¬∞, Pitch: {pitch?.toFixed(2)}¬∞, Yaw: {yaw?.toFixed(2)}¬∞
            </Typography>
          </CardContent>
        </Card>

        {/* Temperature */}
        <Card sx={{ width: '100%', maxWidth: 360 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üå°Ô∏è Temperature</Typography>
            <Typography variant="body1">{temperature?.toFixed(1)} ¬∞C</Typography>
            <LinearProgress
              variant="determinate"
              value={(temperature / 100) * 100}
              color={getColor(temperature, 'temp')}
              sx={{ height: 8, borderRadius: 5, mt: 1 }}
            />
          </CardContent>
        </Card>

        {/* Altitude */}
        <Card sx={{ width: '100%', maxWidth: 360 }}>
          <CardContent>
            <Typography variant="h6" color="primary">üß≠ Altitude</Typography>
            <Typography variant="body1">{altitude?.toFixed(2)} m</Typography>
          </CardContent>
        </Card>
        
      </Stack>
    </Box>
  );
};

export default TelemetryPanel;
