import React, { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';

const bootSequence = [
  'Initializing Drone Command HQ...',
  'Powering up sensors...',
  'Loading Telemetry Core...',
  'Establishing satellite uplink...',
  'Activating visual interface...',
  'System check complete...',
  'Welcome, Commander.',
];

const BootScreen = ({ onComplete }) => {
  const [showFade, setShowFade] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const delayPerLine = 500;
    const totalLineTime = bootSequence.length * delayPerLine;

    // Create audio element
    audioRef.current = new Audio('/sounds/boot.mp3');
    audioRef.current.volume = 0.7;

    const sequenceTimer = setTimeout(() => {
      setShowFade(true);

      // Allow browser to load the sound before playing
      audioRef.current.play().then(() => {
        console.log("Boot sound played âœ…");
      }).catch(err => {
        console.warn("Autoplay may be blocked ðŸ˜¢", err);
      });

      // Transition after sound ends (1s duration)
      setTimeout(() => {
        onComplete();
      }, 1000);
    }, totalLineTime + 500); // Wait for lines + small pause

    return () => {
      clearTimeout(sequenceTimer);
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, [onComplete]);

  return (
    <div
      style={{
        backgroundColor: 'black',
        color: '#00ffe0',
        fontFamily: 'Orbitron, monospace',
        fontSize: '1.2rem',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        paddingLeft: '5vw',
        overflow: 'hidden',
        position: 'relative',
      }}
    >
      {bootSequence.map((line, index) => (
        <motion.div
          key={index}
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: index * 0.5, duration: 0.4 }}
          style={{ marginBottom: '0.8rem', textShadow: '0 0 8px cyan' }}
        >
          {line}
        </motion.div>
      ))}

      {showFade && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.6 }}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'black',
            zIndex: 10,
          }}
        />
      )}
    </div>
  );
};

export default BootScreen;
