// src/components/ConnectionInfoPanel.js

import React from 'react';
import { Card, CardContent, Typography, Stack, Box } from '@mui/material';
import { motion } from 'framer-motion';

const ConnectionInfoPanel = ({ telemetry }) => {
  if (!telemetry || !telemetry.gps) return null;

  const { gps, isConnected } = telemetry;
  const connectionStatus = isConnected ? '‚úÖ Connected' : '‚ùå Disconnected';

  return (
    <motion.div
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <Card
        sx={{
          background: '#111',
          border: '1px solid #333',
          boxShadow: '0 0 10px #0ff',
          color: 'white',
          mb: 2,
        }}
      >
        <CardContent>
          <Typography variant="h6" color="primary" gutterBottom>
            üì° System Status
          </Typography>
          <Stack spacing={1}>
            <Box>
              <Typography variant="body2" color="gray">
                Connection:
              </Typography>
              <Typography
                variant="subtitle1"
                color={isConnected ? 'success.main' : 'error'}
              >
                {connectionStatus}
              </Typography>
            </Box>

            <Box>
              <Typography variant="body2" color="gray">
                GPS Coordinates:
              </Typography>
              <Typography variant="subtitle2">
                Lat: {gps.lat?.toFixed(6)} | Lon: {gps.lon?.toFixed(6)}
              </Typography>
            </Box>
          </Stack>
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default ConnectionInfoPanel;
