// src/api/socket.js

// Simulate fake drone telemetry using setInterval + callback
let listeners = [];

function getRandom(min, max) {
  return +(Math.random() * (max - min) + min).toFixed(2);
}

function generateTelemetryData() {
  return {
    battery: getRandom(7, 12),           // Volts
    altitude: getRandom(10, 100),        // Meters
    temperature: getRandom(20, 70),      // Celsius
    roll: getRandom(-45, 45),            // Degrees
    pitch: getRandom(-45, 45),
    yaw: getRandom(0, 360),
    gps: {
      lat: getRandom(12.97, 12.99),      // Example: Bangalore-ish
      lon: getRandom(77.59, 77.61),
      alt: getRandom(10, 100)
    },
    connection: ["Excellent", "Good", "Poor", "No Signal"][
      Math.floor(Math.random() * 4)
    ],
    timestamp: new Date().toISOString()
  };
}

setInterval(() => {
  const newData = generateTelemetryData();
  listeners.forEach((cb) => cb(newData));
}, 2000); // update every 2 seconds

export function subscribeToTelemetry(callback) {
  listeners.push(callback);
  return () => {
    listeners = listeners.filter((cb) => cb !== callback);
  };
}
