// src/components/TelemetryPanel.js

import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  useTheme,
} from '@mui/material';
import {
  BatteryGauge,
  TemperatureGauge,
  AltitudeGauge,
  IMUGauge,
} from './Gauges';
import {
  BatteryGraph,
  TemperatureGraph,
  AltitudeGraph,
  IMUGraph,
} from './Graphs';

const TelemetryPanel = ({ telemetry, chartData }) => {
  const theme = useTheme();

  const getBatteryStatusColor = (value) => {
    if (value >= 9) return 'success';
    if (value >= 7.4) return 'warning';
    return 'error';
  };

  const getBatteryAlert = (value) => {
    return value < 3.7 ? 'âš ï¸ Battery Critical!' : null;
  };

  const getGPSAlert = (prevLat, newLat, prevLon, newLon) => {
    if (!prevLat || !newLat || !prevLon || !newLon) return null;
    const latDiff = Math.abs(prevLat - newLat);
    const lonDiff = Math.abs(prevLon - newLon);
    return latDiff < 0.0001 && lonDiff < 0.0001 ? 'ðŸ“¡ Lost Signal: Attempting Reconnectâ€¦' : null;
  };

  const getIMUAlert = (yawHistory) => {
    if (!yawHistory || yawHistory.length < 3) return null;
    const recentYaw = yawHistory.slice(-3).map((d) => d.yaw);
    const deltas = recentYaw.slice(1).map((val, i) => Math.abs(val - recentYaw[i]));
    const isErratic = deltas.some((delta) => delta > 90);
    return isErratic ? 'âš ï¸ Instability Detected' : null;
  };

  const batteryAlert = telemetry ? getBatteryAlert(telemetry.battery) : null;
  const gpsAlert =
    telemetry && chartData.batteryHistory.length > 1
      ? getGPSAlert(
          chartData.batteryHistory[chartData.batteryHistory.length - 2]?.lat,
          telemetry.gps?.lat,
          chartData.batteryHistory[chartData.batteryHistory.length - 2]?.lon,
          telemetry.gps?.lon
        )
      : null;
  const imuAlert = getIMUAlert(chartData.imuHistory);

  return (
    <Box p={3}>
      <Grid container spacing={3}>
        {/* Battery */}
        <Grid item xs={12}>
          <Card sx={{ backgroundColor: '#1e293b', color: 'white' }}>
            <CardContent>
              <Typography variant="h6" color="primary">ðŸ”‹ Battery</Typography>
              <Box display="flex" alignItems="center">
                <BatteryGauge value={telemetry?.battery} />
                <BatteryGraph data={chartData.batteryHistory} />
              </Box>
              {batteryAlert && (
                <Chip
                  label={batteryAlert}
                  color="error"
                  sx={{ mt: 2, animation: 'pulse 1s infinite', fontWeight: 'bold' }}
                />
              )}
            </CardContent>
          </Card>
        </Grid>

        {/* Temperature */}
        <Grid item xs={12}>
          <Card sx={{ backgroundColor: '#1e293b', color: 'white' }}>
            <CardContent>
              <Typography variant="h6" color="primary">ðŸŒ¡ Temperature</Typography>
              <Box display="flex" alignItems="center">
                <TemperatureGauge value={telemetry?.temperature} />
                <TemperatureGraph data={chartData.temperatureHistory} />
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Altitude */}
        <Grid item xs={12}>
          <Card sx={{ backgroundColor: '#1e293b', color: 'white' }}>
            <CardContent>
              <Typography variant="h6" color="primary">ðŸ—» Altitude</Typography>
              <Box display="flex" alignItems="center">
                <AltitudeGauge value={telemetry?.altitude} />
                <AltitudeGraph data={chartData.altitudeHistory} />
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* IMU */}
        <Grid item xs={12}>
          <Card sx={{ backgroundColor: '#1e293b', color: 'white' }}>
            <CardContent>
              <Typography variant="h6" color="primary">ðŸŽ¯ IMU (Roll, Pitch, Yaw)</Typography>
              <Box display="flex" alignItems="center">
                <IMUGauge
                  roll={telemetry?.roll}
                  pitch={telemetry?.pitch}
                  yaw={telemetry?.yaw}
                />
                <IMUGraph data={chartData.imuHistory} />
              </Box>
              {imuAlert && (
                <Chip
                  label={imuAlert}
                  color="warning"
                  sx={{ mt: 2, animation: 'pulse 1s infinite', fontWeight: 'bold' }}
                />
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* CSS Animation */}
      <style>
        {`
          @keyframes pulse {
            0% { opacity: 1; }
            50% { opacity: 0.4; }
            100% { opacity: 1; }
          }
        `}
      </style>
    </Box>
  );
};

export default TelemetryPanel;
