// src/components/BootScreen.js
import React from 'react';
import { motion } from 'framer-motion';

const BootScreen = () => {
  return (
    <motion.div
      initial={{ opacity: 1 }}
      animate={{ opacity: 0 }}
      transition={{ duration: 1.5, delay: 2 }}
      style={{
        position: 'fixed',
        top: 0, left: 0,
        width: '100vw',
        height: '100vh',
        background: 'black',
        color: 'cyan',
        fontFamily: 'Orbitron, monospace',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 9999,
      }}
    >
      <motion.h1
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        style={{ fontSize: '2.5rem', textShadow: '0 0 10px cyan' }}
      >
        DRONE COMMAND HQ
      </motion.h1>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 1 }}
        style={{ fontSize: '1.2rem', marginTop: '1rem', textShadow: '0 0 5px cyan' }}
      >
        Initializing systems...
      </motion.p>
    </motion.div>
  );
};

export default BootScreen;
