// src/components/BootScreen.js
import React, { useEffect } from 'react';
import { motion } from 'framer-motion';

const BootScreen = () => {
  useEffect(() => {
    const audio = new Audio('/sounds/boot.mp3');
    audio.volume = 0.7;
    audio.play().catch(err => console.error('Audio playback failed:', err));
  }, []);

  return (
    <motion.div
      initial={{ opacity: 1, scale: 1 }}
      animate={{ opacity: 0, scale: 1.05 }}
      transition={{ duration: 1.5, delay: 3 }}
      style={{
        position: 'fixed',
        top: 0, left: 0,
        width: '100vw',
        height: '100vh',
        background: 'radial-gradient(circle at center, #0f172a 0%, #000 100%)',
        color: '#00f2ff',
        fontFamily: 'Orbitron, monospace',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 9999,
        overflow: 'hidden',
      }}
    >
      <motion.h1
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1 }}
        style={{
          fontSize: '3rem',
          textShadow: '0 0 20px #0ff, 0 0 40px #0ff',
          letterSpacing: '4px',
        }}
      >
        DRONE COMMAND HQ
      </motion.h1>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: [0, 1, 1, 0, 1] }}
        transition={{ duration: 2.5, times: [0, 0.2, 0.5, 0.7, 1], delay: 1 }}
        style={{
          fontSize: '1.3rem',
          marginTop: '1.5rem',
          color: '#0ff',
          fontWeight: 'bold',
          fontFamily: 'monospace',
          borderRight: '2px solid #0ff',
          whiteSpace: 'nowrap',
          overflow: 'hidden',
          width: '17ch',
          animation: 'typing 2.5s steps(17), blink 0.7s step-end infinite',
        }}
      >
        Initializing Systems...
      </motion.p>

      <style>{`
        @keyframes typing {
          from { width: 0 }
          to { width: 17ch }
        }
        @keyframes blink {
          50% { border-color: transparent }
        }
      `}</style>
    </motion.div>
  );
};

export default BootScreen;
