import React from 'react';
import { Box, Card, CardContent, Typography } from '@mui/material';
import BatteryGauge from './gauges/BatteryGauge';
import TemperatureGauge from './gauges/TemperatureGauge';
import AltitudeGauge from './gauges/AltitudeGauge';
import IMUGauge from './gauges/IMUGauge';
import BatteryGraph from './graphs/BatteryGraph';
import TemperatureGraph from './graphs/TemperatureGraph';
import AltitudeGraph from './graphs/AltitudeGraph';
import IMUGraph from './graphs/IMUGraph';

const cardStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  backgroundColor: '#1e293b',
  color: 'white',
  mb: 3,
  p: 2,
  border: '1px solid #334155',
};

const alertStyle = {
  color: 'red',
  fontWeight: 'bold',
  fontSize: '0.9rem',
  marginTop: '0.5rem',
};

const TelemetryPanel = ({ telemetry, chartData, alerts }) => {
  return (
    <Box sx={{ p: 3 }}>
      {/* Battery */}
      <Card sx={cardStyle}>
        <CardContent>
          <Typography variant="h6" color="primary">🔋 Battery</Typography>
          <BatteryGauge value={telemetry?.battery || 0} />
          {alerts.battery && <Typography sx={alertStyle}>{alerts.battery}</Typography>}
        </CardContent>
        <BatteryGraph data={chartData.batteryHistory} />
      </Card>

      {/* Temperature */}
      <Card sx={cardStyle}>
        <CardContent>
          <Typography variant="h6" color="primary">🌡️ Temperature</Typography>
          <TemperatureGauge value={telemetry?.temperature || 0} />
        </CardContent>
        <TemperatureGraph data={chartData.temperatureHistory} />
      </Card>

      {/* Altitude */}
      <Card sx={cardStyle}>
        <CardContent>
          <Typography variant="h6" color="primary">📏 Altitude</Typography>
          <AltitudeGauge value={telemetry?.altitude || 0} />
        </CardContent>
        <AltitudeGraph data={chartData.altitudeHistory} />
      </Card>

      {/* IMU */}
      <Card sx={cardStyle}>
        <CardContent>
          <Typography variant="h6" color="primary">🎯 IMU (Roll, Pitch, Yaw)</Typography>
          <IMUGauge
            roll={telemetry?.roll || 0}
            pitch={telemetry?.pitch || 0}
            yaw={telemetry?.yaw || 0}
          />
          {alerts.imu && <Typography sx={alertStyle}>{alerts.imu}</Typography>}
        </CardContent>
        <IMUGraph data={chartData.imuHistory} />
      </Card>
    </Box>
  );
};

export default TelemetryPanel;
