// src/components/TopBar.js
import React, { useEffect, useState } from 'react';
import { AppBar, Toolbar, Typography, Box, Chip, Stack, Avatar } from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import BatteryFullIcon from '@mui/icons-material/BatteryFull';
import SignalCellularAltIcon from '@mui/icons-material/SignalCellularAlt';
import GpsFixedIcon from '@mui/icons-material/GpsFixed';

const getBatteryColor = (battery) => {
  if (battery < 3.7) return 'error';
  if (battery < 7) return 'warning';
  return 'success';
};

const getSignalColor = (connection) => {
  if (connection === 'Excellent') return 'success';
  if (connection === 'Poor') return 'warning';
  return 'error';
};

const TopBar = ({ telemetry }) => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const battery = telemetry?.battery;
  const connection = telemetry?.connection;
  const gps = telemetry?.gps;

  return (
    <AppBar position="static" sx={{ background: '#111', borderBottom: '2px solid #00f2ff' }}>
      <Toolbar sx={{ justifyContent: 'space-between' }}>
        <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
          üöÅ Drone Dashboard
        </Typography>

        <Stack direction="row" spacing={2} alignItems="center">
          {/* Battery */}
          <Chip
            icon={<BatteryFullIcon />}
            label={`${battery ? ((battery / 12) * 100).toFixed(0) : 0}%`}
            color={getBatteryColor(battery)}
            variant="outlined"
          />

          {/* GPS */}
          <Chip
            icon={<GpsFixedIcon />}
            label={
              gps
                ? `${gps.lat.toFixed(2)}, ${gps.lon.toFixed(2)}`
                : 'No GPS'
            }
            color="info"
            variant="outlined"
          />

          {/* Time */}
          <Chip
            icon={<AccessTimeIcon />}
            label={time.toLocaleTimeString()}
            color="secondary"
            variant="outlined"
          />

          {/* Signal */}
          <Chip
            icon={<SignalCellularAltIcon />}
            label={connection || 'No Signal'}
            color={getSignalColor(connection)}
            variant="outlined"
          />
        </Stack>
      </Toolbar>
    </AppBar>
  );
};

export default TopBar;
