import React, { useEffect, useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Stack,
  LinearProgress,
  Chip,
} from '@mui/material';
import { subscribeToTelemetry } from '../api/socket';

// Helper to get MUI color tokens based on value/type
const getColor = (value, type) => {
  if (type === 'battery') {
    if (value < 3.7) return 'error';
    if (value < 7) return 'warning';
    return 'primary';
  } else if (type === 'temp') {
    if (value > 70) return 'error';
    if (value > 50) return 'warning';
    return 'primary';
  } else if (type === 'connection') {
    if (value === 'Excellent') return 'success';
    if (value === 'Poor') return 'warning';
    return 'error';
  }
};

const TelemetryPanel = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const unsubscribe = subscribeToTelemetry((incoming) => {
      console.log('üì° Received telemetry:', incoming);
      setData(incoming);
    });
    return () => unsubscribe();
  }, []);

  if (!data) return <Typography color="gray">Connecting...</Typography>;

  const {
    battery = 0,
    temperature = 0,
    connection = 'Unknown',
    gps = { lat: 0, lon: 0, alt: 0 },
    imu = { roll: 0, pitch: 0, yaw: 0 },
    altitude = 0,
  } = data;

  return (
    <Stack spacing={2} alignItems="center" justifyContent="center" sx={{ width: '100%', pt: 2 }}>
      {/* Battery */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üîã Battery</Typography>
          <Stack direction="row" alignItems="center" spacing={1}>
            <Typography variant="body1">{battery.toFixed(2)} V</Typography>
            <Chip
              label={battery < 3.7 ? 'Low' : battery < 7 ? 'Medium' : 'Good'}
              color={getColor(battery, 'battery')}
              size="small"
            />
          </Stack>
          <LinearProgress
            variant="determinate"
            value={(battery / 12) * 100}
            color={getColor(battery, 'battery')}
            sx={{ height: 8, borderRadius: 5, mt: 1 }}
          />
        </CardContent>
      </Card>

      {/* Connection */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üì° Connection</Typography>
          <Chip
            label={connection}
            color={getColor(connection, 'connection')}
            sx={{ mt: 1 }}
          />
        </CardContent>
      </Card>

      {/* GPS */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üìç GPS</Typography>
          <Typography variant="body1">
            Lat: {gps.lat.toFixed(4)}, Lon: {gps.lon.toFixed(4)}
          </Typography>
          <Typography variant="body2">
            Alt: {gps.alt.toFixed(2)} m
          </Typography>
        </CardContent>
      </Card>

      {/* IMU */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üìà IMU</Typography>
          <Typography variant="body1">
            Roll: {imu?.roll?.toFixed(2) ?? '0.00'}¬∞, Pitch: {imu?.pitch?.toFixed(2) ?? '0.00'}¬∞, Yaw: {imu?.yaw?.toFixed(2) ?? '0.00'}¬∞
          </Typography>
        </CardContent>
      </Card>

      {/* Temperature */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üå°Ô∏è Temperature</Typography>
          <Stack direction="row" alignItems="center" spacing={1}>
            <Typography variant="body1">{temperature.toFixed(1)} ¬∞C</Typography>
            <Chip
              label={
                temperature > 70 ? 'Hot' : temperature > 50 ? 'Warm' : 'Normal'
              }
              color={getColor(temperature, 'temp')}
              size="small"
            />
          </Stack>
          <LinearProgress
            variant="determinate"
            value={(temperature / 100) * 100}
            color={getColor(temperature, 'temp')}
            sx={{ height: 8, borderRadius: 5, mt: 1 }}
          />
        </CardContent>
      </Card>

      {/* Altitude */}
      <Card sx={{ width: '100%', maxWidth: 360 }}>
        <CardContent>
          <Typography variant="h6" color="primary">üß≠ Altitude</Typography>
          <Typography variant="body1">{altitude.toFixed(2)} m</Typography>
        </CardContent>
      </Card>
    </Stack>
  );
};

export default TelemetryPanel;
